actionConfiguration.body:
(data, key, selOption) => {
  return data.filter(row => {
    let keys = key.split("."), len = keys.length;
    let value = len == 1 ? row[key] : len == 2 ? row[keys[0]][keys[1]] : "";
    return value == selOption || selOption == "ALL";
  });
}

